INSERT INTO `topics` (`topic_id`, `parent_topic_id`, `topic_title`, `level`, `description`, `created_time`, `icon_name`) VALUES
(8, NULL, 'THỂ LOẠI SÁCH', 6, 'Đọc sách là nhu cầu của tất cả mọi người, bởi lẽ sách là phương tiện học tập thuận lợi, giúp con người nâng cao nhận thức, hiểu biết. Sách là kho tàng tri thức mà nhân loại tạo ra, lưu lại, truyền cho thế hệ sau. Đó là nguồn tri thức rất quan trọng và vô ', '2017-12-15 23:21:46', 'th'),
(9, NULL, 'THỂ LOẠI TRUYỆN', 6, '“Ở trường học, bạn học được một bài học, sau đó mới kiểm tra. Ở trường đời, chính bài kiểm tra mà bạn phải làm sẽ cho bạn một bài học xứng đáng\" - Tom Bodett', '2017-12-15 23:23:19', 'th'),
(10, NULL, 'TẢN VĂN & TỰ TRUYỆN', 6, 'Light Novel (ライトノベル raito noberu) là một dòng tiểu thuyết Nhật Bản vốn nhắm vào giới độc giả là học sinh trung học cơ sở hay trung học phổ thông.', '2017-12-15 08:36:06', 'th'),
(13, 8, 'PHÁT TRIỂN BẢN THÂN', 6, 'Những bí quyết thành công đáng ngạc nhiên của các thành viên sáng giá nhất', '2017-12-15 08:57:07', 'th'),
(15, 8, 'SÁCH VĂN HỌC', 6, 'Sách Văn Học – Halibi || Nơi cập nhập sách văn học trong nước và nước ngoài – Một trong những dòng sách có lượng đọc giả và giải thưởng văn chương nhiều nhất trên thế giới', '2018-12-27 01:56:53', 'th'),
